#!/bin/perl -w
#
# https://adventofcode.com/2015/day/1

use strict;
use POSIX;

print "2015 Day 1\n";
my $floor = 0;
my $hit_basement = 0;
while (<DATA>) {
	chomp;
	my @a = split //;
	for (my $x = 0; $x < scalar(@a); $x++) {
		if ($a[$x] eq ')') {
			$floor--;
		} elsif ($a[$x] eq '(') {
			$floor++;
		}
		if (not $hit_basement and $floor == -1) {
			print "P2: First basement visit on position " . ($x+1) . "\n";
			$hit_basement = 1;
		}
	}
}

print "P1: Final destination: floor $floor\n";

