#!/bin/perl -w
#
# https://adventofcode.com/2015/day/3

use strict;
use POSIX;

$| = 1;

my $directions = <DATA>;
chomp $directions;

print "2015 Day 3, Part 1\n";
my $x = 0;
my $y = 0;
my %seen = ( "0,0" => 1 );
my $len = length $directions;
for (my $i = 0; $i < $len; $i++) {
	my $char = substr $directions, $i, 1;
	if ($char eq '^') {
		$y--;
	} elsif ($char eq 'v') {
		$y++;
	} elsif ($char eq '<') {
		$x--;
	} elsif ($char eq '>') {
		$x++;
	} else {
		warn "Unknown direction $char at index $x\n";
		next;
	}
	$seen{"$x,$y"} = 0 if (not exists $seen{"$x,$y"});
	$seen{"$x,$y"}++;
}

print "P1: Santa visited " . scalar(keys(%seen)) . " houses at least once.\n";

print "\n2015 Day 3, Part 2\n";
$x = 0;
$y = 0;
my $rx = 0;
my $ry = 0;
%seen = ( "0,0" => 2 );
for (my $i = 0; $i < $len; $i+=2) {
	# Santa
	my $char = substr $directions, $i, 1;
	if ($char eq '^') {
		$y--;
	} elsif ($char eq 'v') {
		$y++;
	} elsif ($char eq '<') {
		$x--;
	} elsif ($char eq '>') {
		$x++;
	} else {
		warn "Unknown direction $char at index $i\n";
		next;
	}
	$seen{"$x,$y"} = 0 if (not exists $seen{"$x,$y"});
	$seen{"$x,$y"}++;
	# Robo-Santa
	my $char = substr $directions, $i+1, 1;
	if ($char eq '^') {
		$ry--;
	} elsif ($char eq 'v') {
		$ry++;
	} elsif ($char eq '<') {
		$rx--;
	} elsif ($char eq '>') {
		$rx++;
	} else {
		warn "Unknown direction $char at index " . ($i+1) . "\n";
		next;
	}
	$seen{"$rx,$ry"} = 0 if (not exists $seen{"$rx,$ry"});
	$seen{"$rx,$ry"}++;
}

print "P2: Santa & Robo-Santa visited " . scalar(keys(%seen)) . " houses at least once.\n";

